mapper.insert:
INSERT INTO contributor (member_id, project_id)
VALUES (
    (SELECT id FROM member WHERE user_id = #{param1}),
    #{param2}
)

mapper.select:
SELECT user_id
FROM member
WHERE id = #{memberId}

mapper.update:
UPDATE project
SET name = #{name},
    public_range = #{public_range},
    start_at = #{start_at},
    end_at = #{end_at}
WHERE id = #{project_id}

mapper.delete:
DELETE FROM contributor
WHERE project_id = #{projectId}
  AND member_id = #{contributorId}

mapper.update:
UPDATE project
SET del_chk = 1
WHERE id = #{project_id}
